mapper.select:
select count(cartNum) from cart_aa
where idx_fk=#{idx_fk}
and pnum_fk=#{pnum_fk}

mapper.update:
update cart_aa set
oqty=oqty + #{oqty}
where idx_fk=#{idx_fk} and pnum_fk=#{pnum_fk}

mapper.select:
select sum(totalPrice) cartTotalPrice,
	   sum(totalPoint) cartTotalPoint
	   from CartView_aa where idx_fk=#{value}
